define(['common'],function(){

    var addCon = $('.addPerson-content');
    //添加
    var form = addCon.html();
    $('.add-btn').on('click', function () {
        $('.add-level').append('<div class="row addPerson-content">' + form + '</div>' + '<br>');
    });

    //删除
    $(document).on('click', '.delete-btn', function () {
        $(this).parents('.addPerson-content').remove();
    });

})


